"use strict";(self.webpackChunkimmersve_docs=self.webpackChunkimmersve_docs||[]).push([[4116],{74428:e=>{e.exports=JSON.parse('{"label":"authentication","permalink":"/tags/authentication","allTagsPath":"/tags","count":2,"items":[{"id":"guides/custodial wallets/authentication","title":"Authentication","description":"API clients consuming the custodial wallet API endpoints use API key authentication to authenticate to the API. API keys are issued to the custodial wallet provider by Immersve and are used to authorize requests to the relevant custodial wallet endpoints.","permalink":"/guides/custodial wallets/authentication"},{"id":"guides/non-custodial wallets/authentication","title":"Authentication","description":"Accounts are identified by way of blockchain addresses. Proof of ownership of an address is based upon a process of authentication by way of verification of EIP-4361 challenges signed by the private key underlying an address. Upon successful authentication, Immersve will issue an access token for subsequent use in interactions with the Immersve API.","permalink":"/guides/non-custodial wallets/authentication"}]}')}}]);