"use strict";(self.webpackChunkimmersve_docs=self.webpackChunkimmersve_docs||[]).push([[4116],{74428:e=>{e.exports=JSON.parse('{"label":"authentication","permalink":"/tags/authentication","allTagsPath":"/tags","count":1,"items":[{"id":"guides/authentication","title":"Authentication","description":"Accounts are identified by way of blockchain addresses. Proof of ownership of an address is based upon a process of authentication by way of verification of EIP-4361 challenges signed by the private key underlying an address. Upon successful authentication, Immersve will issue an access token for subsequent use in interactions with the Immersve API.","permalink":"/guides/authentication"}]}')}}]);